import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormText from './FormText';

export default class FormDialog extends React.Component{

    constructor(props){
        super(props);
        this.state= {
            name : "",
            email : "",
            description : ""
        }
        this.inputName = this.inputName.bind(this)
        this.inputEmail = this.inputEmail.bind(this)
        this.inputDescription = this.inputDescription.bind(this)
    }
    //したの３つはstateの更新をしている。
    //１文字いれるた
    inputName = (event) =>{
        this.setState({
            name:event.target.value
        })
    }
    inputEmail = (event) =>{
        this.setState({
            email:event.target.value
        })
    }
    inputDescription = (event) =>{
        this.setState({
            description:event.target.value
        })
    }
    render(){
        return(
            <Dialog
            //なぜthisが必要のか
                open={this.props.open}
                onClose={this.props.handleClickClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">お問い合わせ</DialogTitle>
                <DialogContent>
                    <FormText
                        multiline = {false}
                        rows = {1}
                        type = {"text"}
                        label = {"お名前（必須）"}
                        onChange = {this.inputName}
                        value = {this.state.name}
                    />
                    <FormText
                        multiline = {false}
                        rows = {1}
                        type = {"email"}
                        label = {"メールアドレス（必須）"}
                        onChange = {this.inputEmail}
                        value = {this.state.email}
                    />
                    <FormText
                        multiline = {true}
                        rows = {5}
                        type = {"text"}
                        label = {"お問い合わせ内容（必須）"}
                        onChange = {this.inputDescription}
                        value = {this.state.description}
                    />
                </DialogContent>
                <DialogActions>
                <Button onClick={this.props.handleClickClose} color="primary">
                    Disagree
                </Button>
                <Button onClick={this.props.handleClickClose} color="primary" autoFocus>
                    Agree
                </Button>
                </DialogActions>
            </Dialog>
        )
    }
}
