import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import FormText from './FormDialog';

export default class FormDialog extends React.Component{

    constructor(props){
        super(props);
        this.state= {
            name : "",
            email : "",
            description : ""
        }
        this.inputName = this.inputName.bind(this)
        this.inputEmail = this.inputEmail.bind(this)
        this.inputDescription = this.inputDescription.bind(this)
    }
    inputName = (event) =>{
        this.setState({
            name:event.target.value
        })
    }
    inputEmail = (event) =>{
        this.setState({
            email:event.target.value
        })
    }
    inputDescription = (event) =>{
        this.setState({
            description:event.target.value
        })
    }
    render(){
        return(
            <Dialog
            //なぜthisが必要のか
                open={this.props.open}
                onClose={this.props.handleClickClose}
                aria-labelledby="alert-dialog-title"
                aria-describedby="alert-dialog-description"
            >
                <DialogTitle id="alert-dialog-title">{"Use Google's location service?"}</DialogTitle>
                <DialogContent>
                    <FormText
                        multiline = {false}
                        rows = {1}
                        value = {"text"}
                        label = {"お名前（必須）"}
                        onChange = {this.inputName}
                        type = {this.state.name}
                    />
                </DialogContent>
                <DialogActions>
                <Button onClick={this.props.handleClickClose} color="primary">
                    Disagree
                </Button>
                <Button onClick={this.props.handleClickClose} color="primary" autoFocus>
                    Agree
                </Button>
                </DialogActions>
            </Dialog>
        )
    }
}
